    [Inject]
    public IJSRuntime JS { get; set; }

    string matricula;

    int zoom = 7;
    string marca;
    string modelo;
    int año;
    string color;
    double precio;
    string tipo = "Carro";
    int capacidad;
    int pasajeros;
    long seguro;
    string foto;
    double latitud;
    double longitud;


    void OnMapClick(GoogleMapClickEventArgs args)
    {
        latitud = args.Position.Lat;
        longitud = args.Position.Lng;
    }

    public async Task searchVehicle()
    {

        String API = $"https://pruebasv4.herokuapp.com/SearchEnrollment/{matricula}";

        WebClient cliente = new WebClient();

        var datos = await cliente.DownloadStringTaskAsync(API);

        var resultados = JsonConvert.DeserializeObject<Vehicle>(datos);

        marca = resultados.BRAND;
        modelo = resultados.MODEL;
        año = resultados.YEAR;
        color = resultados.COLOUR;
        precio = resultados.PRICEPERDAY;
        tipo = resultados.TYPE;
        capacidad = (int.TryParse(resultados.LOADCAPACITY, out int number)) ? number : number;
        pasajeros = resultados.PASSENGERS;
        seguro = (long.TryParse(resultados.INSURANCE_NO, out long number2)) ? number2 : number2;
        foto = resultados.PHOTO;
        latitud = (double.TryParse(resultados.LATITUDE, out double number3)) ? number3 : number3;
        longitud = (double.TryParse(resultados.LONGITUDE, out double number4)) ? number4 : number4;
    }

    public async Task editVehicle()
    {

        var http = new HttpClient();

        var vehicle = new Vehicle
        {
            BRAND = marca,
            MODEL = modelo,
            YEAR = año,
            COLOUR = color,
            PRICEPERDAY = precio,
            TYPE = tipo,
            LOADCAPACITY = $"{capacidad}",
            PASSENGERS = pasajeros,
            INSURANCE_NO = $"{seguro}",
            Enrollment = matricula,
            PHOTO = foto,
            LATITUDE = $"{latitud}",
            LONGITUDE = $"{longitud}"
        };

        var jsonBody = JsonConvert.SerializeObject(vehicle);
        var buffer = Encoding.UTF8.GetBytes(jsonBody);
        var byteContent = new ByteArrayContent(buffer);
        byteContent.Headers.ContentType = new MediaTypeHeaderValue("application/json");

        var response = await http.PutAsync("https://pruebasv4.herokuapp.com/updatevehicle/"+matricula, byteContent);

        if (response.IsSuccessStatusCode)
        {
            await JS.InvokeVoidAsync("alert", "Registro editado exitosamente");
            uriHelper.NavigateTo(uriHelper.Uri, forceLoad: true);
        }
        else
        {
            await JS.InvokeVoidAsync("alert", "Revise los datos que ha agregado y vuelva a intentar");
        }
    }

    public async Task LoadFile(InputFileChangeEventArgs e)
    {
        try
        {
            using var reader = new StreamReader(e.File.OpenReadStream());

            string file = await reader.ReadToEndAsync();

            foto = $"data:{e.File.ContentType};base64," + Convert.ToBase64String(Encoding.ASCII.GetBytes(file));
        }
        catch (Exception ex)
        {
            Console.WriteLine(ex.Message);
        }
    }

