    [Inject]
    public IJSRuntime JS { get; set; }


    string user;
    string pass;

    public async Task userLogin()
    {
       
        var http = new HttpClient();

        var loguearse = new LOGINUP
        {
            username = user,
            password = pass
        };

        var jsonBody = JsonConvert.SerializeObject(loguearse);
        var buffer = Encoding.UTF8.GetBytes(jsonBody);
        var byteContent = new ByteArrayContent(buffer);
        byteContent.Headers.ContentType = new MediaTypeHeaderValue("application/json");

        var response = await http.PostAsync("https://pruebasv4.herokuapp.com/login", byteContent);

        if (response.IsSuccessStatusCode)
        {

            uriHelper.NavigateTo("/home", forceLoad: true);

        }
        else
        {
            await JS.InvokeVoidAsync("alert", "Usuario/Contraseña incorrectas");
        }

    }


    public class LOGINUP
    {
        public string username { get; set; }
        public string password { get; set; }
    }

